/* eslint-disable import/prefer-default-export */
import { LOAD_COURSES, LOAD_COURSES_BY_RHYTHM } from '../types/courseType';
import { getCourses } from '../service/courseService';

export const fetchCourses = () => async (dispatch) => {
  const courses = await getCourses();
  dispatch({
    type: LOAD_COURSES,
    payload: courses.data,
  });
};

export const fetchCoursesByRhythm = () => async (dispatch) => {
  const search = await getSearchByRhythm();
  dispatch({
    type: LOAD_COURSES_BY_RHYTHM,
    payload: search.data,
  });
};
