/* eslint-disable jsx-a11y/no-noninteractive-element-interactions */
/* eslint-disable jsx-a11y/no-static-element-interactions */
/* eslint-disable jsx-a11y/click-events-have-key-events */
/* eslint-disable jsx-a11y/anchor-is-valid */
import { useState } from 'react';
import { FaChevronDown, FaChevronUp } from 'react-icons/fa';
import { useDispatch } from 'react-redux';
import { fetchCoursesByRhythm } from '../../store/actions/courseActions';

const Dropdown = () => {
  const dispatch = useDispatch();
  const [showDropdown, setShowDropdown] = useState(false);
  const toggleOpen = () => {
    setShowDropdown(!showDropdown);
  };

  const search = (e) => {
    dispatch(fetchCoursesByRhythm(e.target.value));
  };

  return (
    <div className=" font-sans py-10 mr-20">
      <div className="relative">
        <button
          type="button"
          className="flex "
        >
          <span className=" text-white text-2xl">
            Courses
          </span>
          <span className="text-white mt-2" onClick={toggleOpen}>
            {showDropdown ? <FaChevronUp className="w-10" />
              : <FaChevronDown className="w-10" />}
          </span>
        </button>
        {showDropdown
        && (
          <ul className="text-center z-10">
            <li
              className="p-5 bg-black text-white font-serif z-10"
              onClick={search}
              value="salsa"
            >
              Salsa
            </li>
            <li
              className="p-5 bg-black text-white font-serif z-10"
              onClick={search}
              value="bachata"
            >
              Bachata
            </li>
            <li
              className="p-5 bg-black text-white font-serif z-10"
              onClick={search}
              value="kizomba"
            >
              Kizomba

            </li>
          </ul>
        )}
      </div>
    </div>
  );
};

export default Dropdown;
