/* eslint-disable jsx-a11y/anchor-is-valid */
/* eslint-disable no-underscore-dangle */
import { useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
// import { getCourse } from '../../store/service/courseService';
const URL_BASE = process.env.REACT_APP_API_URL_BASE;
console.log('url', URL_BASE);

const DetailTeacher = async () => {
  const [course, setCourse] = useState();
  const { id } = useParams();
  console.log('id', id);

  // console.log('id in effe', id);
  const fetchCourse = await axios.get(`${URL_BASE}/api/course/${id}`);
  console.log('fetchCourse', fetchCourse);
  setCourse(fetchCourse.data);
  // useEffect(async () => {
  //   console.log('id in effe', id);
  //   const fetchCourse = await axios.get(`${URL_BASE}/api/course/${id}`);
  //   console.log('fetchCourse', fetchCourse);
  //   setCourse(fetchCourse.data);
  // }, [id]);

  return (
    <div className="flex">
      <div>
        <img className="chat-notification-logo" src={course.image} alt="ChitChat Logo" />
      </div>
      <div className="chat-notification-content">
        <h4 className="chat-notification-title">{course.title}</h4>
        <span>{course.teacher}</span>
        <p className="chat-notification-message">{course.description}</p>
        <span>{course.place}</span>
      </div>
    </div>
  );
};

export default DetailTeacher;
