/* eslint-disable no-case-declarations */
/* eslint-disable default-param-last */
import { SIGN_UP_SUCCESSFUL, ROLE, UPDATE_SUCCESSFUL } from '../types/userTypes';
// import { getCurrentLocalStorage } from '../utils/LocalStorageUtils';

const initialState = {
  user: {},
  // token: JSON.parse(localStorage.getItem(`${item}`)) ?? '',
  // role: '',
  // toke: '',
};

const userReducer = (state = initialState, action) => {
  console.log('action', action.payload);
  switch (action.type) {
    case SIGN_UP_SUCCESSFUL:
      return { ...state, token: action.payload };
      // const { name, email, image } = action.payload;
      // return { ...state, user: { ...state.user, name, email, image } };
    case ROLE:
      const role = action.payload;
      return { ...state, user: { ...state.user, role } };
    case UPDATE_SUCCESSFUL:
      // const  = action.payload;
      return { ...state, user: action.payload };
    // case LOGOUT:
    //   return {};
    default:
      return state;
  }
};

export default userReducer;
