/* eslint-disable jsx-a11y/no-noninteractive-element-interactions */
/* eslint-disable jsx-a11y/click-events-have-key-events */
import { useState } from 'react';
import { FaBars } from 'react-icons/fa';

const NavBar = () => {
  const [showMenu, setShowMenu] = useState(false);
  console.log(showMenu);
  return (
    <>
      <nav>
        <ul className="flex bg-black h-20 text-white">
          <li className="flex-1 text-3xl text-center pt-4 pl-4">U Dance</li>
          <li
            className="text-3xl pt-5 pr-4"
            onClick={() => setShowMenu(true)}
          >
            <FaBars />
          </li>
        </ul>
      </nav>
      {/* className={`${showMenu ? 'top-20' : 'top-[-490px]'} */}
      <ul className="text-center">
        <li className={`${showMenu ? 'absolute right-20' : 'absolute right-0'} p-5 text-white`}>Home</li>
        <li className={`${showMenu ? 'absolute right-20' : 'absolute right-0'} p-5 text-white`}>Course</li>
        <li className={`${showMenu ? 'absolute right-20' : 'absolute right-0'} p-5 text-white`}>Log in</li>
        <li className={`${showMenu ? 'absolute right-20' : 'absolute right-0'} p-5 text-white`}>Sign up</li>
      </ul>
    </>
  );
};

export default NavBar;
