/* eslint-disable quotes */
/* eslint-disable prefer-template */
/* eslint-disable react/style-prop-object */
/* eslint-disable jsx-a11y/anchor-is-valid */
/* eslint-disable no-underscore-dangle */
import { useDispatch, useSelector } from 'react-redux';
import { useEffect } from 'react';
import { Link } from 'react-router-dom';
import CardCourse from '../../components/CardCourse';
import { fetchCourses } from '../../store/actions/courseActions';

const LandingPage = () => {
  const dispatch = useDispatch();
  const courses = useSelector((state) => state.course.courses);
  useEffect(() => {
    dispatch(fetchCourses());
  }, []);
  // const img = 'https://images.unsplash.com/photo-1623479322729-28b25c16b011?ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=1740&q=80';

  return (
    <>
      <section className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-4 mb-12">
        <article>
          <section className="mt-6 grid grid-cols-1 md:grid-cols-1 lg:grid-cols-3 gap-x-6 gap-y-8">
            <article
              className="bg-photo-landing relative w-full h-64 bg-cover bg-center group rounded-lg overflow-hidden shadow-lg hover:shadow-2xl  transition duration-300 ease-in-out"
            >
              <div className="absolute inset-0 bg-black bg-opacity-50 group-hover:opacity-75 transition duration-300 ease-in-out" />
              <div className="relative w-full h-full px-4 sm:px-6 lg:px-4 flex justify-center items-center">
                <h3 className="text-center">
                  <a className="text-white text-2xl font-bold text-center" href="#">
                    <span className="absolute inset-0" />
                    Top 10 highest paid programming languages of 2021
                  </a>
                </h3>
              </div>
            </article>
            {/* <article
              className="relative w-full h-64 bg-cover bg-center group rounded-lg overflow-hidden shadow-lg hover:shadow-2xl  transition duration-300 ease-in-out"
            >
              <div className="absolute inset-0 bg-black bg-opacity-50 group-hover:opacity-75 transition duration-300 ease-in-out" />
              <div className="relative w-full h-full px-4 sm:px-6 lg:px-4 flex justify-center items-center">
                <h3 className="text-center">
                  <a className="text-white text-2xl font-bold text-center" href="#">
                    <span className="absolute inset-0" />
                    Python Frameworks
                  </a>
                </h3>
              </div>
            </article> */}
            <article
              className="relative w-full h-64 bg-cover bg-center group rounded-lg overflow-hidden shadow-lg hover:shadow-2xl  transition duration-300 ease-in-out"
            >
              <div className="absolute inset-0 bg-black bg-opacity-50 group-hover:opacity-75 transition duration-300 ease-in-out" />
              <div className="relative w-full h-full px-4 sm:px-6 lg:px-4 flex justify-center items-center">
                <h3 className="text-center">
                  <a className="text-white text-2xl font-bold text-center" href="#">
                    <span className="absolute inset-0" />
                    The best plugins for Visual Studio Code
                  </a>
                </h3>
              </div>
            </article>
          </section>
        </article>
      </section>
      {/* <div className=""> */}
      {/* <img className="w-8/12 ml-auto mr-auto" src="https://res.cloudinary.com/db3njhxi0/image/upload/v1644634655/U-Dance/WhatsApp_Image_2022-02-11_at_9.56.11_PM_iwu2a1.jpg" alt="" />
      <div className="absolute top-56 right-96 left-80 text-white text-center">
        <h1>U Dance</h1>
        <p>ClASESS, COURSES</p>
      </div> */}
      {/* </div> */}
      <div className="bg-black">
        <div className="flex flex-wrap justify-around">
          {courses.map((course) => (
            <Link
              to={`/teacherDetail/${course._id}`}
              key={course._id}
              style={{ textDecoration: 'none' }}
            >
              <CardCourse course={course} />
            </Link>
          ))}
        </div>
      </div>
    </>
  );
};

export default LandingPage;
