/* eslint-disable no-underscore-dangle */
/* eslint-disable jsx-a11y/no-noninteractive-element-interactions */
/* eslint-disable jsx-a11y/no-static-element-interactions */
import { useState } from 'react';
import { FaChevronDown, FaChevronUp } from 'react-icons/fa';
import { Link } from 'react-router-dom';

const Dropdown = () => {
  const [showDropdown, setShowDropdown] = useState(false);
  const toggleOpen = () => {
    setShowDropdown(!showDropdown);
  };

  const rhythm = ['salsa', 'bachata'];

  return (
    <div className=" font-sans py-10 mr-20">
      <div className="relative">
        <button
          type="button"
          className="flex "
        >
          <span className=" text-white text-2xl">
            Courses
          </span>
          <span className="text-white mt-2" onClick={toggleOpen}>
            {showDropdown ? <FaChevronUp className="w-10" />
              : <FaChevronDown className="w-10" />}
          </span>
        </button>
        {showDropdown
        && (
          <ul className="text-center z-10 absolute">
            {rhythm.map((item) => (
              <li
                className="p-5 bg-black text-white font-serif z-50"
                value="salsa"
              >
                <Link
                  to={`/pages/search/${item}`}
                  key={item}
                  style={{ textDecoration: 'none' }}
                >
                  {item}
                </Link>
              </li>
            )) }
          </ul>
        )}
      </div>
    </div>
  );
};

export default Dropdown;
