/* eslint-disable quotes */
/* eslint-disable prefer-template */
/* eslint-disable react/style-prop-object */
/* eslint-disable jsx-a11y/anchor-is-valid */
/* eslint-disable no-underscore-dangle */
import { useDispatch, useSelector } from 'react-redux';
import { useEffect } from 'react';
import { Link } from 'react-router-dom';
import CardCourse from '../../components/CardCourse';
import { fetchCourses } from '../../store/actions/courseActions';

const LandingPage = () => {
  const dispatch = useDispatch();
  const courses = useSelector((state) => state.course.courses);
  useEffect(() => {
    dispatch(fetchCourses());
  }, []);

  return (
    <>
      <section className="max-w-8xl mx-auto sm:px-6 text-center w-full h-full">
        {/* <article> */}
        <section className="bg-photo-landing bg-no-repeat bg-center w-full h-full">
          <article
            className="relative  h-96 bg-cover bg-center group rounded-lg overflow-hidden
             shadow-lg hover:shadow-2xl  transition duration-300 ease-in-out"
          >
            <div className="absolute inset-0 bg-black bg-opacity-20 w-2/4 ml-auto mr-auto
             group-hover:opacity-75
            transition duration-300 ease-in-out"
            />
            <div className="relative w-full h-full px-4 sm:px-6 flex justify-center
             items-center"
            >
              <h3 className="text-center">
                <a className="text-white text-2xl font-bold text-center" href="#">
                  <span className="absolute inset-0" />
                  Top 10 highest paid programming languages of 2021
                </a>
              </h3>
            </div>
          </article>
        </section>
        {/* </article> */}
      </section>
      <div className="bg-black">
        <div className="flex flex-wrap justify-around">
          {courses.map((course) => (
            <Link
              to={`/teacherDetail/${course._id}`}
              key={course._id}
              style={{ textDecoration: 'none' }}
            >
              <CardCourse course={course} />
            </Link>
          ))}
        </div>
      </div>
    </>
  );
};

export default LandingPage;
