/* eslint-disable jsx-a11y/no-static-element-interactions */
/* eslint-disable jsx-a11y/click-events-have-key-events */
/* eslint-disable jsx-a11y/anchor-is-valid */
/* eslint-disable no-underscore-dangle */
import { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { getCourse } from '../../store/service/courseService';
// bg-[url('https://res.cloudinary.com/db3njhxi0/image/upload/v1644705077/U-Dance/neon-city-02_ldvpti.jpg')]

const DetailTeacher = () => {
  const [course, setCourse] = useState();
  const { id } = useParams();

  useEffect(async () => {
    const fetchCourse = await getCourse(id);
    setCourse(fetchCourse.data);
  }, []);

  return (
    <div className="h-screen
     from-cyan-500 to-blue-500 flex flex-wrap text-center justify-evenly m-10 font-serif font-bold"
    >
      <div>
        <img className="chat-notification-logo" src={course?.image} alt="ChitChat Logo" />
      </div>
      <div className="w-2/4">
        <h4 className="text-2xl">{course?.title.toUpperCase()}</h4>
        <p className="text-2xl">{course?.teacher.toUpperCase()}</p>
        <p className="pt-6">{course?.description.toUpperCase()}</p>
        <p className="pt-4">{course?.place.toUpperCase()}</p>
      </div>
    </div>
  );
};

export default DetailTeacher;
